import React from 'react';
import axios from 'axios';
// import { Link } from 'react-router-dom';

class Berita extends React.Component {
  state = {
    datas: [],
    isLoading: true,
    errors: null
  };

  getdatas() {
    axios
      .get(
        'https://newsapi.org/v2/top-headlines?' +
          'country=us&' +
          'apiKey=API_KEY'
      )
      .then(response =>
        response.data.results.map(user => ({
          name: `${user.articles.source.name}`,
          title: `${user.articles.title}`,
          description: `${user.articles.description}`,
          content: `${user.articles.content}`
        }))
      )
      .then(datas => {
        this.setState({
          datas,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }

  componentDidMount() {
    this.getDatas();
  }

  render() {
    const { isLoading, datas } = this.state;
    return (
      <React.Fragment>
        <h2>News Portal</h2>
        {/* <p>
          Ini di dapat dari{' '}
          <Link to='https://randomuser.me/'>Random User API</Link>
        </p> */}
        <div>
          {!isLoading ? (
            datas.map(data => {
              const { name, title, description, content } = data;
              return (
                <div key={name}>
                  <h2>{title}</h2>
                  <h3>{name}</h3>
                  <div>
                    <h3>{description}</h3>
                  </div>
                  <p>{content}</p>
                  <hr />
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default Berita;
