    //   .then(data => this.setState({items: data, isLoading:false }))
  }

  render() {
    const {items, isLoading} = this.state


    return isLoading ? <p>loading . . .</p> : <div>
        <ul>
          {items.map((item, index) => <li key={index}>{item.name}</li> )}
          <li key></li>
        </ul>
    </div>;
  }
}

export default App;

