// import logo from './logo.svg';
import './App.css';

import React, { Component } from 'react';
import List from './List';

class App extends Component {

  constructor(props) {
    super(props);
    this.state ={
      todoItem: '',
      items: []
    }
  }

  handleSubmit = (e) => {
    e.preventDefault() //mencegah reload
    this.setState({
      items: [...this.state.items, this.state.todoItem], 
      //data array, jk kosong y kosong, jk ada isi akan digabung dgn *nilai baru*/ concat
      // *nilai baru didapat dari mana, dari line 39, setiap user merubah atau mengetik dan add akan merubah nilai todo item dan digabung di array items
      todoItem: '' //setelah di klik todoItems akan dikosongkan kembali
    })
    // console.log('Terpanggil')
  }

  handleChange = (e) =>{
    this.setState({
      todoItem: e.target.value 
    })
  }


  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}> {/**ketika tombol add / submit di tekan akan menjalankan  fungsi handleSubmit */}
          <input value={this.state.todoItem} onChange={this.handleChange}></input>
          <button>ADD</button>
        </form>

        {/**semua daftar items di oper semua ke halaman componen List*/}
        <List items={this.state.items}></List> 
      </div>
    );
  }
}

export default App;
